mapper.sql:
AND 





						b.ab_title LIKE #{cri.keyword}


						b.ab_content LIKE #{cri.keyword}


						b.member_userId LIKE #{cri.keyword}

mapper.select:
SELECT b.ab_number, b.ab_category, b.ab_title, b.ab_content, b.member_userId, b.ab_insertDatetime, COUNT(DISTINCT r.ab_replyNumber) replyCnt, COUNT(DISTINCT l.member_userId) countLike, COUNT(DISTINCT f.academyBoard_ab_number) countFile, b.ab_viewCount, m.nickName
FROM academyBoard b LEFT JOIN academyBoardReply r ON b.ab_number = r.academyBoard_ab_number LEFT JOIN academyBoardLike l ON b.ab_number = l.academyBoard_ab_number LEFT JOIN academyBoardFileAttach f ON b.ab_number = f.academyBoard_ab_number LEFT JOIN member m ON b.member_userId = m.userId
WHERE 1=1
AND b.ab_category = #{category}
GROUP BY b.ab_number ORDER BY b.ab_number DESC LIMIT #{offset}, #{records}

mapper.update:
UPDATE academyBoard
SET ab_viewCount = ab_viewCount + 1
WHERE ab_number = #{ab_number}
