mapper.select:
SELECT b.ab_number, b.ab_category, b.ab_title, b.ab_content, b.member_userId, b.ab_insertDatetime, COUNT(r.academyBoard_ab_number) replyCnt
FROM academyBoard b LEFT JOIN academyBoardReply r ON b.ab_number = r.academyBoard_ab_number GROUP BY b.ab_number ORDER BY b.ab_number DESC
LIMIT #{offset}, #{records}

mapper.update:
UPDATE academyBoard
SET ab_replyCount = ab_replyCount + #{amount} 
WHERE ab_number = #{ab_number}
