SELECT f.fb_number, f.fb_category, f.fb_title, f.fb_insertDatetime, f.member_userId, COUNT(r.fb_replyNumber) fb_replyCount FROM freeBoard f LEFT JOIN freeBoardReply r ON f.fb_number = r.freeBoard_fb_number WHERE
OR f.fb_title LIKE #{keyword}
OR f.fb_content LIKE #{keyword}
OR f.member_userId LIKE #{keyword}
GROUP BY f.fb_number ORDER BY f.fb_number DESC LIMIT #{offset}, #{records}

 	SELECT COUNT(*) FROM freeBoard
