mapper.select:
SELECT f.fb_number, f.fb_category, f.fb_title, f.fb_insertDatetime, f.member_userId, f.fb_viewCount, m.nickName, COUNT(DISTINCT r.fb_replyNumber) fb_replyCount, COUNT(DISTINCT l.member_userId) fb_likeCount FROM freeBoard f LEFT JOIN freeBoardReply r ON f.fb_number = r.freeBoard_fb_number LEFT JOIN freeBoardLike l ON f.fb_number = l.freeBoard_fb_number LEFT JOIN member m ON f.member_userId = m.userId WHERE
OR f.fb_title LIKE #{keyword}
OR f.fb_content LIKE #{keyword}
OR f.member_userId LIKE #{keyword}
GROUP BY f.fb_number ORDER BY f.fb_number DESC LIMIT #{offset}, #{records}

 	SELECT COUNT(*) FROM freeBoard
 	WHERE


	 		fb_title LIKE #{keyword}



	 		OR fb_content LIKE #{keyword}



	 		OR member_userId LIKE #{keyword}

mapper.update:
 	UPDATE freeBoard
	SET fb_viewCount = fb_viewCount + 1
	WHERE fb_number = #{fb_number}
