mapper.update:
UPDATE QnABoard SET
	title = #{title},
	content = #{content}		
WHERE
	qnaId = #{qnaId}

mapper.select:
SELECT q.qnaId, q.category, q.title, q.content, q.status, m.userId, m.nickName, COUNT(DISTINCT l.qnaLikeId) AS likeCount, COUNT(DISTINCT r.qnaReplyToAnswerId) AS replyCount, COUNT(DISTINCT f.fileName) AS fileCount
FROM QnABoard q LEFT JOIN member m ON q.member_userId = m.userId LEFT JOIN QnABoardLike l ON q.qnaId = l.qnaId LEFT JOIN QnAReplyToAnswer r ON q.qnaId = r.qnaId LEFT JOIN QnAFiles f ON q.qnaId = f.qnaId WHERE m.userId = #{userId} AND
q.category = #{category} AND
(
OR q.title LIKE #{keyword}
OR q.content LIKE #{keyword}
OR m.userId LIKE #{keyword}
) GROUP BY q.qnaId ORDER BY q.qnaId DESC LIMIT #{offset}, #{records}

SELECT q.qnaId, q.category, q.title, q.content, q.status, q.member_userId AS userId, q.insertDatetime, COUNT(distinct l.qnaLikeId) AS likeCount, COUNT(DISTINCT r.qnaReplyToAnswerId) AS replyCount, COUNT(DISTINCT f.fileName) AS fileCount FROM QnABoard q LEFT JOIN QnABoardLike l ON q.qnaId = l.qnaId LEFT JOIN QnAReplyToAnswer r ON q.qnaId = r.qnaId LEFT JOIN QnAFiles f ON q.qnaId = f.qnaId WHERE q.status = #{status} AND
q.category = #{category} AND
(
OR q.title LIKE #{keyword}
OR q.content LIKE #{keyword}
OR q.member_userId LIKE #{keyword}
) GROUP BY q.qnaId ORDER BY q.qnaId DESC LIMIT #{offset}, #{records}

