SELECT q.qnaId, q.category, q.title, q.content, q.status, q.member_userId AS userId, q.insertDatetime, COUNT(DISTINCT l.qnaLikeId) AS likeCount, COUNT(DISTINCT r.qnaReplyToAnswerId) AS replyCount, COUNT(DISTINCT f.fileName) AS fileCount FROM QnABoard q LEFT JOIN QnABoardLike l ON q.qnaId = l.qnaId LEFT JOIN QnAReplyToAnswer r ON q.qnaId = r.qnaId LEFT JOIN QnAFiles f ON q.qnaId = f.qnaId
